  async handleLogsRefresh() {
    if (!this.logsPodName || !this.logsNamespace) return;
    this.logsLoading = true;
    this.logsError = "";
    try {
      const logs = await KubernetesApi.getPodLogs(this.logsPodName, this.logsNamespace);
      this.logsData = logs;
    } catch (error) {
      console.error("Failed to refresh pod logs:", error);
      this.logsError = error.message || "Failed to refresh logs";
    } finally {
      this.logsLoading = false;
    }
  }
  async fetchData() {
    this.loading = true;
    this.error = null;
    try {
      this.namespaces = await KubernetesApi.getNamespaces();
      const namespace = this.selectedNamespace === "All Namespaces" ? void 0 : this.selectedNamespace;
      let data = [];
      switch (this.activeTab) {
        case "pods":
          const pods = await KubernetesApi.getPods(namespace);
          data = pods.map((pod) => ({ ...pod, type: "Pod" }));
          break;
        case "deployments":
          const deployments = await KubernetesApi.getDeployments(namespace);
          data = deployments.map((dep) => ({ ...dep, type: "Deployment", replicas: dep.ready }));
          break;
        case "statefulsets":
          const statefulsets = await KubernetesApi.getStatefulSets(namespace);
          data = statefulsets.map((sts) => ({ ...sts, type: "StatefulSet", replicas: sts.ready }));
          break;
        case "daemonsets":
          const daemonsets = await KubernetesApi.getDaemonSets(namespace);
          data = daemonsets.map((ds) => ({ ...ds, type: "DaemonSet", replicas: `${ds.ready}/${ds.desired}` }));
          break;
        case "jobs":
          const jobs = await KubernetesApi.getJobs(namespace);
          data = jobs.map((job) => ({ ...job, type: "Job", replicas: job.completions }));
          break;
        case "cronjobs":
          const cronjobs = await KubernetesApi.getCronJobs(namespace);
          data = cronjobs.map((cj) => ({ ...cj, type: "CronJob", replicas: cj.schedule }));
          break;
      }
      this.workloads = data;
    } catch (error) {
      console.error("Failed to fetch workloads:", error);
      this.error = error.message || "Failed to fetch workloads";
    } finally {
      this.loading = false;
    }
  }
