  formatBytes(bytes) {
    if (bytes === 0) return "0 B";
    const k2 = 1024;
    const sizes = ["B", "KB", "MB", "GB", "TB"];
    const i4 = Math.floor(Math.log(bytes) / Math.log(k2));
    return `${(bytes / Math.pow(k2, i4)).toFixed(1)} ${sizes[i4]}`;
  }
  formatMemory(kb) {
    if (kb >= 1024 * 1024) {
      return `${(kb / (1024 * 1024)).toFixed(1)} GB`;
    } else if (kb >= 1024) {
      return `${(kb / 1024).toFixed(0)} MB`;
    }
    return `${kb} KB`;
  }
